import { useState } from 'react';
import { Box, InputLabel, TextField, Grid, Select, MenuItem } from '@material-ui/core';
import { Formik } from 'formik';
import * as Yup from 'yup';
import useStyles from './useStyles';

const airports = [
    {value: 1, name: 'Istanbul Airport'}, 
    {value: 2, name:'Sabiha Airport'}
 ];
const resorts = [
    {value: 1, name: 'Sultan Ahmet'},
    {value: 2, name: 'Sirkeci'},
    {value: 3, name: 'Taksim'},
    {value: 4, name: 'Fatih'},
    {value: 5, name: 'Laleli'}
];

function NewReservationForm(): JSX.Element {
    const classes = useStyles();

    const handleSubmit = () => {console.log('submitting')};
    return (
        <>
            <Box>
                <Formik
                    initialValues={{
                        firstName: '',
                        lastName: '',
                        type: 0,
                        from: 0,
                        to: 0,
                        hotel: '',
                        pax: 1,
                        flightNo: '',
                        driverNote: ''
                    }}
                    validationSchema={Yup.object().shape({ 
                        firstName: Yup.string().required('FirstName is required!'),
                        lastName: Yup.string().required('LastName is required!'),
                        type: Yup.string().required(),
                        from:  Yup.string().required(),
                        to: Yup.string().required(),
                        hotel: Yup.string().required(),
                        flightNo: Yup.string().required(),
                        driverNote: Yup.string(),
                        noOfPpl: Yup.number().min(1).max(9),
                    })}
                    onSubmit={handleSubmit}
                >   
                    {({ handleSubmit, handleChange, values, touched, errors, isSubmitting }) => (
                    <form onSubmit={handleSubmit} className={classes.form} noValidate>
                        <Grid container direction='column' alignItems='center' justify='center'>
                            <Grid item container spacing={4}>
                                <Grid item>
                                    <InputLabel id="type" className={classes.inputLabel}>
                                        type
                                    </InputLabel>
                                    <Select
                                        id="type"
                                        name="type"
                                        autoWidth
                                        value={values.type}
                                        onChange={handleChange}
                                        classes={{ select: classes.select }}
                                        >
                                        <MenuItem value={0}>---Select---</MenuItem>
                                        <MenuItem value={1}>Airport to Resort</MenuItem>
                                        <MenuItem value={2}>Resort to Airport</MenuItem>
                                    </Select>
                                </Grid>
                                <Grid item>
                                    <InputLabel className={classes.inputLabel}>
                                        from
                                    </InputLabel>
                                    <Select
                                        id="from"
                                        name="from"
                                        autoWidth
                                        value={values.from}
                                        onChange={handleChange}
                                        classes={{ select: classes.select }}
                                        >
                                        <MenuItem value={0}>---Select---</MenuItem>
                                        {values.type === 1 ? airports.map((airport) => (
                                            <MenuItem value={airport.value}>{airport.name}</MenuItem>
                                        )): resorts.map((resort) => (
                                            <MenuItem value={resort.value}>{resort.name}</MenuItem>
                                        ))
                                        }
                                    </Select>
                                </Grid>
                                <Grid item>
                                    <InputLabel className={classes.inputLabel}>
                                        to
                                    </InputLabel>
                                    <Select
                                        id="to"
                                        name="to"
                                        autoWidth
                                        value={values.to}
                                        onChange={handleChange}
                                        classes={{ select: classes.select }}
                                        >
                                        <MenuItem value={0}>---Select---</MenuItem>
                                        {values.type === 2 ? airports.map((airport) => (
                                            <MenuItem value={airport.value}>{airport.name}</MenuItem>
                                        )): resorts.map((resort) => (
                                            <MenuItem value={resort.value}>{resort.name}</MenuItem>
                                        ))
                                        }
                                    </Select>
                                </Grid>
                            </Grid>
                            <Grid item container spacing={2}>
                                <Grid item>
                                    <InputLabel className={classes.inputLabel}>
                                        first name
                                    </InputLabel>
                                    <TextField 
                                        id='firstName'
                                        name='firstName'
                                        InputProps= {{
                                            classes: { input: classes.inputs },
                                            disableUnderline: true
                                        }}
                                        value={values.firstName}
                                        onChange={handleChange}

                                    />
                                </Grid>
                                <Grid item>
                                    <InputLabel className={classes.inputLabel}>
                                        last name
                                    </InputLabel>
                                    <TextField 
                                        id='lastName'
                                        name='lastName'
                                        InputProps= {{
                                            classes: { input: classes.inputs },
                                            disableUnderline: true
                                        }}
                                        value={values.lastName}
                                        onChange={handleChange}
                                    />
                                </Grid>
                            </Grid>
                                <Grid item container spacing={2}>
                                    <Grid item>
                                        <InputLabel className={classes.inputLabel}>
                                            hotel
                                        </InputLabel>
                                        <TextField 
                                            id='hotel'
                                            name='hotel'
                                            InputProps= {{
                                                classes: { input: classes.inputs },
                                                disableUnderline: true
                                            }}
                                            value={values.hotel}
                                            onChange={handleChange}

                                        />
                                    </Grid>
                                    <Grid item>
                                        <InputLabel className={classes.inputLabel}>
                                            pax
                                        </InputLabel>
                                        <TextField 
                                            id='pax'
                                            name='pax'
                                            error={Boolean(errors.pax)}
                                            helperText={errors.pax}
                                            InputProps= {{
                                                classes: { input: classes.inputs },
                                                disableUnderline: true
                                            }}
                                            value={values.pax}
                                            onChange={handleChange}
                                        />
                                    </Grid>
                                </Grid>
                                <Grid item container spacing={2}>
                                    <Grid item>
                                        <InputLabel className={classes.inputLabel}>
                                            flight no
                                        </InputLabel>
                                        <TextField 
                                            id='flightNo'
                                            name='flightNo'
                                            InputProps= {{
                                                classes: { input: classes.inputs },
                                                disableUnderline: true
                                            }}
                                            value={values.flightNo}
                                            onChange={handleChange}

                                        />
                                    </Grid>
                                    <Grid item>
                                        <InputLabel className={classes.inputLabel}>
                                            driver note
                                        </InputLabel>
                                        <TextField 
                                            id='driverNote'
                                            name='driverNote'
                                            InputProps= {{
                                                classes: { input: classes.inputs },
                                                disableUnderline: true
                                            }}
                                            value={values.driverNote}
                                            onChange={handleChange}
                                        />
                                    </Grid>
                                </Grid>
                        </Grid>
                    </form>
                    )}
                </Formik>
            </Box>
        </>
    )
}
export default NewReservationForm;
