import { useState } from 'react';
import { Box, InputLabel, TextField, Grid, Select, MenuItem } from '@material-ui/core';
import { Formik } from 'formik';
import * as Yup from 'yup';
import useStyles from './useStyles';

function NewReservationForm(): JSX.Element {
    const classes = useStyles();
    // const [type, setType] = useState<string>('Airport to Resort')
    const airports = ['Istanbul Airport', 'Sabiha Airport'];
    const Resorts = ['Sultan Ahmet', 'Sirkeci', 'Taksim', 'Fatih']
    const handleSubmit = () => {console.log('submitting')};
    return (
        <>
            <Box>
                <Formik
                    initialValues={{
                        firstName: '',
                        lastName: '',
                        type: 0,
                        from: 0,
                        to: 0,
                        hotel: '',
                        NoOfPpl: 1,
                        flightNo: '',
                        driverNote: ''
                    }}
                    validationSchema={Yup.object().shape({ 
                        firstName: Yup.string().required('FirstName is required!'),
                        lastName: Yup.string().required('LastName is required!'),
                        type: Yup.string().required(),
                        from:  Yup.string().required(),
                        to: Yup.string().required(),
                        hotel: Yup.string().required(),
                        flightNo: Yup.string().required(),
                        driverNote: Yup.string(),
                        noOfPpl: Yup.number().min(0),
                    })}
                    onSubmit={handleSubmit}
                >   
                    {({ handleSubmit, handleChange, values, touched, errors, isSubmitting }) => (
                    <form onSubmit={handleSubmit} className={classes.form} noValidate>
                        <Grid container direction='column'>
                            <Grid item container spacing={2}>
                                <Grid item>
                                    <InputLabel className={classes.inputLabel}>
                                        first name
                                    </InputLabel>
                                    <TextField 
                                        id='firstName'
                                        name='firstName'
                                        InputProps= {{
                                            classes: { input: classes.inputs },
                                            disableUnderline: true
                                        }}
                                        value={values.firstName}
                                        onChange={handleChange}

                                    />
                                </Grid>
                                <Grid item>
                                    <InputLabel className={classes.inputLabel}>
                                        last name
                                    </InputLabel>
                                    <TextField 
                                        id='lastName'
                                        name='lastName'
                                        InputProps= {{
                                            classes: { input: classes.inputs },
                                            disableUnderline: true
                                        }}
                                        value={values.lastName}
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item container spacing={4}>
                                    <Grid item>
                                        <InputLabel id="type" className={classes.inputLabel}>
                                            type
                                        </InputLabel>
                                        <Select
                                            id="type"
                                            name="type"
                                            autoWidth
                                            value={values.type}
                                            onChange={handleChange}
                                            // className={classes.select}
                                            classes={{ select: classes.select }}
                                            >
                                            <MenuItem value={0}>---Select---</MenuItem>
                                            <MenuItem value={1}>Airport to Resort</MenuItem>
                                            <MenuItem value={2}>Resort to Airport</MenuItem>
                                        </Select>
                                    </Grid>
                                    <Grid item>
                                        <InputLabel className={classes.inputLabel}>
                                            from
                                        </InputLabel>
                                        <Select
                                            id="from"
                                            name="from"
                                            autoWidth
                                            value={values.from}
                                            onChange={handleChange}
                                            classes={{ select: classes.select }}
                                            >
                                            <MenuItem value={0}>---Select---</MenuItem>
                                            <MenuItem value={1}>Istanbul Airport</MenuItem>
                                            <MenuItem value={2}>Sabiha Airport</MenuItem>
                                        </Select>
                                    </Grid>
                                    <Grid item>
                                        <InputLabel className={classes.inputLabel}>
                                            to
                                        </InputLabel>
                                        <Select
                                            id="to"
                                            name="to"
                                            autoWidth
                                            value={values.to}
                                            onChange={handleChange}
                                            classes={{ select: classes.select }}
                                            >
                                            <MenuItem value={0}>---Select---</MenuItem>
                                            <MenuItem value={1}>Sultan Ahmet</MenuItem>
                                            <MenuItem value={2}>Sirkeci</MenuItem>
                                            <MenuItem value={3}>Taksim</MenuItem>
                                            <MenuItem value={4}>Fatih</MenuItem>
                                        </Select>
                                    </Grid>
                                </Grid>
                            </Grid>

                        </Grid>
                    </form>
                    )}
                </Formik>
            </Box>
        </>
    )
}
export default NewReservationForm;
