import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
    root: {
        width: 136,
    },
    menu: {
        backgroundColor: theme.palette.primary.main,
        color: theme.palette.common.white,
        marginTop: "3em",
        opacity: 0.7,
        "&:hover": {
            opacity: 1
        }
    },
    menuItem: {
        opacity: 0.7,
        "&:hover": {
            opacity: 1
        },
        backgroundColor: '#808080',

    },
    tabStyle: {
        fontWeight: 'bold'
    }
}));

export default useStyles;
