import { Grid, InputLabel, Select, MenuItem, TextField } from '@material-ui/core';
import { useEffect, useRef, useState } from 'react';
import CustomButton from '../Button/CustomButton';
import useStyles from './useStyles';

interface Column { field: string; headerName: string } 

interface Props {
    columns: Column [],
    rows: any [],
    setRows: any
}

const TableSearch = ({ columns, rows, setRows }: Props): JSX.Element => {

    const { label, select, inputs } = useStyles();
    const [value, setValue] = useState<string>('');
    const [column, setColumn] = useState<string>('');
    const rowsCache = useRef<any>([]);

    const handleChange = (event: any) => {
        setColumn(event.target.value);
    };

    const handleValueChange = (event: any) => {
        setValue(event.target.value);
    };

    const handleSearchClick = () => {
        setRows(rows.filter(row => row[column] == value));
    };

    useEffect(() => {
        rowsCache.current = rows;
    }, []);

    useEffect(() => {
        if (value == '') {
            console.log('rowsCache is: ', rowsCache);
            setRows(rowsCache.current);
        }
    }, [column, value])

    return (
        <>
            <Grid container justifyContent='center' spacing={3}>
                <Grid item>
                    <InputLabel className={label}>
                        Search
                    </InputLabel>
                    <Select
                        id="businessType"
                        name="businessType"
                        autoWidth
                        MenuProps={{
                            anchorOrigin: {
                                vertical: "bottom",
                                horizontal: "left"
                            },
                            transformOrigin: {
                                vertical: "top",
                                horizontal: "left"
                            },
                            getContentAnchorEl: null
                        }}
                        value={column}
                        onChange={handleChange}
                        classes={{ select: select }}
                    >
                        {columns.map((col, idx) => (
                            <MenuItem key={idx}value={col.field} style={{ width: '100%' }}>{col.headerName}</MenuItem>
                        ))}
                    </Select>
                </Grid>
                <Grid item style={{  marginTop: '1.25em' }}> 
                    <TextField
                        id="value"
                        name="value"
                        onChange={handleValueChange}
                        helperText={value}
                        InputProps={{
                            classes: { input: inputs },
                            disableUnderline: true
                        }}
                    />
                    <CustomButton btnText='search' style='searchButton' onClick={handleSearchClick}/>
                </Grid>
            </Grid>

        </>
    )
};

export default TableSearch;
