// import { useLanguage } from '../../../context/useLanguageContext';

import { Menu, MenuItem } from "@material-ui/core";
import IconButton from "@material-ui/core/IconButton";
import PersonIcon from '@material-ui/icons/Person';
// const Logout = () => {
//     const { logout } = useAuth();
//     const { language } = useLanguage();

//     const handleLogout = () => {
//         logout();
//     };

//     return (
//         <CustomButton style='logout' btnText={language === 'eng' ? 'logout' : 'Çıkış'} onClick={handleLogout}/>
//     );
// };

// export default Logout;s

const Logout = (): JSX.Element => {
    const [anchorEl, setAnchorEl] = useState(null);
    const [open, setOpen] = useState(false);
    const { menu, menuItem, root, tabStyle } = useStyles();

    const handleClick = (e: any) => {
        setAnchorEl(e.currentTarget);
        setOpen(true);
    }

    const handleClose = () => {
        setAnchorEl(null);
        setOpen(false);
    }
    return (
        <>
            <IconButton aria-label="fingerprint" color="secondary">
                <PersonIcon style={{ color: 'black'} }/>
            </IconButton>
            <Menu 
                id="simple-menu"
                anchorEl={anchorEl} 
                open={open} 
                onClose={handleClose}
                classes={{paper: menu}}
                MenuListProps={{onMouseLeave: handleClose}}
                elevation={0}
            >
                <MenuItem classes={{root: menuItem}}  onClick={() => { handleMenuItemClick('eng')}}>ENG-Enlish</MenuItem>
                <MenuItem classes={{root: menuItem}}  onClick={() => { handleMenuItemClick('tr')}}>TR-Turkish</MenuItem>
            </Menu>
    </>
    )
}

export default Logout;
