import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    form: {
        width: '100%',
        marginTop: theme.spacing(1),
        '& .MuiFormControl-root': {
            marginTop: theme.spacing(1),
        },
    },
    inputs: {
        borderRadius: 5,
        border: '2px solid',
        borderColor: 'blue'

    }
}));

export default useStyles;
