import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
    root: {
        // position: 'absolute',
        // right: 10,
        // top: 0,
    },
    menu: {
        backgroundColor: 'transparent',
        color: theme.palette.common.white,
        marginTop: "3em",
        opacity: 0.7,
        "&:hover": {
            opacity: 1
        }
    },
    menuItem: {
        opacity: 0.7,
        "&:hover": {
            opacity: 1
        }

    },
}));

export default useStyles;
