import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    columnHeader: {
        // backgroundColor: '#A9A9A9',
        color: '#000000',
        fontWeight: 900,
        fontSize: 16,
        width: '100%',
      },
      root: {
        backgroundColor: 'primary',
        color: 'primary',
        height: 700, 
        width: '100%'
      },
      rowColor: {
        '&.Open': {
          backgroundColor: '#7FFFD4',
        }
      },

}));

export default useStyles;
