import { Formik } from "formik";
import * as Yup from 'yup';
import useStyles from "./useStyles";
import { Box, Grid, InputLabel, TextField, Select, MenuItem } from '@material-ui/core';

function AddClientForm(): JSX.Element {
    const handleSubmit = () => {console.log('submitting')};
    const classes = useStyles();

    return (
       <>
        <Formik
            initialValues={{
                email: '',
                firstName: '',
                lastName: '',
                businessType: '',
                maxFourPaxCost: 0,
                maxSixPaxCost: 0,
                maxTenPaxCost: 0,
            }}
            validatoinSchema={Yup.object().shape({
                email: Yup.string().email('Invalid Email Address').required('Please Enter Email Address'),
                firstName: Yup.string().required('Please Enter First Name'),
                lastName: Yup.string().required('Please Enter Last Name'),
                businessType: Yup.string().required('please Enter Bussiness Type'),
                maxFourPaxCost: Yup.number().min(0).required('Please enter the cost for (1 - 4) Pax'),
                maxsixPaxCost: Yup.number().min(0).required('Please enter the cost for (1 - 6) Pax'),
                maxtenPaxCost: Yup.number().min(0).required('Please enter the cost for (1 - 10) Pax'),
            })}
            onSubmit={handleSubmit}
        >
            {({ handleSubmit, handleChange, values, touched, errors, isSubmitting }) => (
                <form onSubmit={handleSubmit} className={classes.form} noValidate>
                    <Grid container direction="column" spacing={2}>
                        <Grid item container spacing={2}>
                            <Grid item>
                                <InputLabel className={classes.label}>
                                    first name
                                </InputLabel>
                                <TextField
                                    id="firstName"
                                    name="firstName"
                                    value={values.firstName}
                                    onChange={handleChange}
                                    error={Boolean(errors.firstName)}
                                    helperText={errors.firstName}
                                    InputProps={{
                                        classes: { input: classes.inputs },
                                        disableUnderline: true
                                    }}
                                />
                            </Grid>
                            <Grid item>
                                <InputLabel className={classes.label}>
                                    last name
                                </InputLabel>
                                <TextField
                                    id="lastName"
                                    name="lastName"
                                    value={values.lastName}
                                    onChange={handleChange}
                                    error={Boolean(errors.lastName)}
                                    helperText={errors.lastName}
                                    InputProps={{
                                        classes: { input: classes.inputs },
                                        disableUnderline: true
                                    }} 
                                />
                            </Grid>
                        </Grid>
                        <Grid item container spacing={2}>
                            <Grid item>
                                <InputLabel className={classes.label}>
                                email
                                </InputLabel>
                                <TextField
                                    id="email"
                                    name="email"
                                    value={values.email}
                                    onChange={handleChange}
                                    error={Boolean(errors.email)}
                                    helperText={errors.email}
                                    InputProps={{
                                        classes: { input: classes.inputs },
                                        disableUnderline: true
                                    }}
                                />
                            </Grid>
                            <Grid item>
                                <InputLabel className={classes.label}>
                                    businessType
                                </InputLabel>
                                <Select
                                    id="businessType"
                                    name="businessType"
                                    autoWidth
                                    MenuProps={{
                                        anchorOrigin: {
                                            vertical: "bottom",
                                            horizontal: "left"
                                        },
                                        transformOrigin: {
                                            vertical: "top",
                                            horizontal: "left"
                                        },
                                        getContentAnchorEl: null
                                    }}
                                    value={values.businessType}
                                    onChange={handleChange}
                                    classes={{ select: classes.select }}
                                >
                                    <MenuItem value={0}>Airport to Resort</MenuItem>
                                    <MenuItem value={1}>Resort to Airport</MenuItem>
                                </Select>
                            </Grid>
                        </Grid>
                    </Grid>
                </form> 
            )}


        </Formik>
       </>
    )
};

export default AddClientForm;
