import { useState } from 'react';  
import useStyles from './useStyles';
import { Link } from 'react-router-dom';
import { Tabs, Tab, Box, Menu, MenuItem } from '@material-ui/core';
import { AgencyTabsDictionary } from '../../../utils/dictionary';
import { useLanguage } from '../../../context/useLanguageContext';

const { englishTabs,  turkishTabs } = AgencyTabsDictionary;


function AgencyHeader(): JSX.Element {
    const classes = useStyles();
    const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
    const [open, setOpen] = useState<boolean>(false);

    const { language } = useLanguage();

    const options = [
        'All Drivers',
        'Add Driver',
      ];

    let tabs = [];

    const handleClose = () => {
        setAnchorEl(null);
      };

    const handleMenuItemClick = (
        event: React.MouseEvent<HTMLElement>,
        index: number,
    ) => {
        setSelectedIndex(index);
        setAnchorEl(null);
    };

    function tabFormation() {
        if (language === 'tr') tabs = turkishTabs;
        else tabs = englishTabs;
        return tabs.map(({ label, to }, idx) => (
            <Tab
                key={idx}
                label={label}
                component={Link}
                to={to}
                className={classes.tabs}
            />
        ));
    };

    return (
        <Box className={classes.root}>
            <Tabs
                aria-label="wrapped label tabs example"
                TabIndicatorProps={{
                    style: {
                        display: "none",
                    },
                }}
            >
                {tabFormation()}
            </Tabs>
            <Menu
                id="lock-menu"
                anchorEl={anchorEl}
                open={open}
                onClose={handleClose}
                MenuListProps={{
                'aria-labelledby': 'lock-button',
                role: 'listbox',
                }}
            >
                {options.map((option, index) => (
                <MenuItem
                key={option}
                disabled={index === 0}
                selected={index === selectedIndex}
                onClick={(event) => handleMenuItemClick(event, index)}
                >
                {option}
                </MenuItem>
            ))}
      </Menu>
        </Box>
    )
}

export default AgencyHeader;
