import { Box, MenuItem, Tab, Tabs, Select, InputLabel, Menu } from "@material-ui/core";
import { SetStateAction, useState } from "react";
import useStyles from "../useStyles";

function LanguageTab(): JSX.Element {
    const [value, setValue] = useState('eng');
    const [anchorEl, setAnchorEl] = useState(null);
    const [open, setOpen] = useState(false);
    const classes = useStyles();

    const handleClick = (e: any) => {
        setAnchorEl(e.currentTarget);
        setOpen(true);
    }

    const handleClose = () => {
        setAnchorEl(null);
        setOpen(false);
    }

    const handleChange = (e: any, value: number) => {
        setValue(value)
    }

    return (
        <Box display="flex" flexDirection="column">
            <Tabs 
                value={value} 
                onChange={handleChange}
                TabIndicatorProps={{
                style: {
                    display: "none",
                },
                }} 
            >
                    <Tab
                        aria-owns={anchorEl ? "simple-menu" : undefined}
                        aria-haspopup={anchorEl ? true : undefined}
                        onMouseOver={(event: any) => handleClick(event)}
                        label="Language" 
                    />
            </Tabs>
            <Menu 
                    id="simple-menu"
                    anchorEl={anchorEl} 
                    open={open} 
                    onClose={handleClose}
                    classes={{paper: classes.menu}}
                    MenuListProps={{onMouseLeave: handleClose}}
                    elevation={0}
                >
                    <MenuItem classes={{root: classes.menuItem}}  onClick={() => { handleClose(); setValue('eng')}}>eng</MenuItem>
                </Menu>
        </Box>
    )
}

export default LanguageTab;
