import { useEffect, useState } from 'react';
import useStyles from './useStyles';
import { Box, Button } from '@material-ui/core';
import DataTable from '../../components/DataTable/DataTable';
import { clientReservationsDictionary } from '../../utils/dictionary';
import { engColumn } from '../../utils/dictionary';
import { getReservations } from '../../helpers/APICalls/reservation';
import { GetReservationApiData } from '../../interface/Reservation';
import { GridColumns } from '@material-ui/data-grid';
import { deleteReservation } from '../../helpers/APICalls/reservation';
import { useSnackBar } from '../../context/useSnackbarContext';


// const { engColumns, turksihColumns } = clientReservationsDictionary;
const { turksihColumns } = clientReservationsDictionary;


let lan = 'eng';

function ListReservations(): JSX.Element{
    const classes = useStyles();
    const [rows, setRows] = useState<any>([]);
    const { updateSnackBarMessage } = useSnackBar();

    const handleClick = (cellValues: any) => {
        console.log('this is clicked from here here we are', cellValues);
        const id = cellValues.row._id;
        deleteReservation(id).then((data) => {
            if (data.error) {
                updateSnackBarMessage(data.error.message);
            } else if (data.success) {
                updateSnackBarMessage('reservation cenceled!');
                setRows(rows.filter((row: any) => row.id !== cellValues.id));
            } else {
                updateSnackBarMessage('An unexpected error occurred. Please try again !');
            }
        })

    };

    const columns = engColumn(handleClick);

    console.log(rows)
    useEffect(() => {
        getReservations().then((data) => {
            if (data.error){
                console.log(data.error)
            } else if (data.success) {
                setRows(data.success.rows)
                data.success.rows.map((row, idx) => {
                    row.id = idx + 1;
                })
            } else {
                console.log('Externam error')
            }
        })
        return () => {
            setRows([]);
        }
    }, []);

    return (
        <Box className={classes.root}>
            <DataTable rows={rows} columns={lan === 'tr' ? turksihColumns : columns} />
        </Box>
    )
}

