import { Box, Grid, Paper, Typography } from '@material-ui/core';
import {  useEffect } from 'react';
import NewReservationForm from './NewReservationForm/NewReservationForm';
import useStyles from './useStyles';
import { useHistory } from 'react-router-dom';

const engPage = {
    title: 'add reservation',
    form: {
        type: 'type',
        from: 'from',
        to: 'to',
        pax: 'pax',
        hotel: 'hotel',
        fullName: 'full name',
        passenger: 'passenger',
        driverNote: 'driver note',
        flightNo: 'flight no',
    }
};

const turkishPage = {   
    title: 'reservasyon ekle',
    form: {
        type: 'tip',
        from: 'nerden',
        to: 'nereye',
        pax: 'pax',
        hotel: 'otel',
        fullName: 'ad soyad',
        passenger: 'yolcu',
        driverNote: 'sürücü notu',
        flightNo: 'uçuş no',
    }

}

let page = {title: 'reservasy ekle', form: turkishPage.form, handleSubmit: () => {console.log('submitting')}};
let lan = 'tr';


function NewReservation(): JSX.Element {
    const classes = useStyles();
    const history = useHistory();
    const handleSubmit = () => {console.log('Submitting')};

    useEffect(() => { 
        if (lan === 'tr') page = turkishPage; 
        else page = engPage;
        page.handleSubmit = () => {console.log('Submitting')}
    }, [history])

    return (
        <>
            <Grid container component={Paper} className={classes.root} alignItems='center'>
                <Grid item xs={12} sm={12} md={12} lg={12} >
                    <Box
                        display="flex"
                        flexDirection="column"
                        minHeight="100vh"
                    >
                        <Box>
                            <Typography variant="h2" color="primary" className={classes.title}>
                                { page.title} 
                            </Typography>
                            <NewReservationForm form={page.form}/>
                        </Box>
                    </Box>
                </Grid>
            </Grid>
        </>
    )
}

export default NewReservation;
