import axios from 'axios';
import { Driver, DriverApiData, GetDriversApiData } from '../../interface/Driver';

export const postDriver = async (inputs: Driver):Promise<DriverApiData> => {
    return await axios.post('https://airport-shuttle-server.herokuapp.com/driver', inputs)
        .then((res) => res.data)
        .catch((error) => error.response.data);
};

export const updateDriver = async (inputs: Driver, id: string | undefined):Promise<DriverApiData> => {
    return await axios.patch(`https://airport-shuttle-server.herokuapp.com/driver/${id}`, inputs)
        .then((res) => res.data)
        .catch((error) => error.response.data);
};

export const deleteDriver = async (id: string | undefined):Promise<DriverApiData> => {
    return await axios.delete(`https://airport-shuttle-server.herokuapp.com/driver/${id}`)
        .then((res) => res.data)
        .catch((error) => error.response.data);
};

export const getDrivers = async (): Promise<GetDriversApiData> => {
    return await axios.get('https://airport-shuttle-server.herokuapp.com/driver/all', { withCredentials: true })
        .then((res) => { 
            console.log(data)
             res.data })
        .catch(() => ({
            error: {message: 'Unable to connect to server. Please try again'}
        }));
};
