import { Grid, Typography } from "@material-ui/core";
import { Client } from "../../../interface/Client";
import AddClientFrom from '../AddClient/AddClientForm/AddClientForm';
import useStyles from "./useStyles";

interface Props {
    values: Client
};

const UpdateClient = ({ values }: Props): JSX.Element => {
    const { root, title } = useStyles();

    const handleSubmit = () => {
        updateClient(inputs).then((data) => {
            if (data.error) {
                console.log(data.error);
                updateSnackBarMessage(data.error.message);
            } else if (data.success){
                console.log(data.success);
                updateSnackBarMessage('Client has been added successfully!')
            } else {
                updateSnackBarMessage('An unexpected error occurred. Please try again !');
            }
        });
    }
    return (
        <>
            <Grid container direction="column" className={root} justify="center">
                    <Typography className={title}>
                        Update Client
                    </Typography>
                <Grid item>
                    <AddClientFrom handleSubmit={handleSubmit} values={values}/>
                </Grid>
            </Grid>
        </>
    )
};

export default UpdateClient;
