import { Form, Formik } from "formik";
import * as Yup from 'yup';
import useStyles from "./useStyles";
import { Grid, InputLabel, TextField, Select, MenuItem, Typography, Button, CircularProgress } from '@material-ui/core';
import CustomButton from "../../../../components/Button/CustomButton";
import { Client } from '../../../../interface/Client';

interface Props {
    handleSubmit: (inputs: Client) => void;
}

function AddClientForm({ handleSubmit }: Props): JSX.Element {
    const classes = useStyles();
    const onSubmit = (e: { preventDefault: () => void; }) =>  {
        e.preventDefault()
        console.log('form is submitted')
    }

    return (
       <>
        <Formik
            initialValues={{
                email: '',
                firstName: '',
                lastName: '',
                businessType: '',
                address: '',
                propertyName: '',
                IstAirportMaxFourPaxCost: 0,
                IstAirportMaxSixPaxCost: 0,
                IstAirportMaxTenPaxCost: 0,
                SawAirportMaxFourPaxCost: 0,
                SawAirportMaxSixPaxCost: 0,
                SawAirportMaxTenPaxCost: 0,
            }}
            validationSchema={Yup.object().shape({
                email: Yup.string().email('Invalid Email Address').required('Please Enter Email Address'),
                firstName: Yup.string().required('This field can not be blank'),
                lastName: Yup.string().required('This field can not be blank'),
                propertyName: Yup.string().required('This field can not be blank'),
                address: Yup.string().required('This field can not be blank'),
                businessType: Yup.string().required('This field can not be blank'),
                maxFourPaxCost: Yup.number().min(0).required('Please enter the cost for (1 - 4) Pax'),
                maxsixPaxCost: Yup.number().min(0).required('Please enter the cost for (1 - 6) Pax'),
                maxtenPaxCost: Yup.number().min(0).required('Please enter the cost for (1 - 10) Pax'),
            })} 
            onSubmit={(values) => handleSubmit(values)}

        >
            {({ handleSubmit, handleChange, values, touched, errors, isSubmitting }) => (
                <Form onSubmit={onSubmit} className={classes.form} noValidate>
                    <Grid container direction="column" spacing={2}>
                        <Grid item container justify="space-evenly">
                            <Grid item>
                                <InputLabel className={classes.label}>
                                    first name
                                </InputLabel>
                                <TextField
                                    id="firstName"
                                    name="firstName"
                                    value={values.firstName}
                                    onChange={handleChange}
                                    error={Boolean(errors.firstName)}
                                    helperText={errors.firstName}
                                    InputProps={{
                                        classes: { input: classes.inputs },
                                        disableUnderline: true
                                    }}
                                />
                            </Grid>
                            <Grid item>
                                <InputLabel className={classes.label}>
                                    last name
                                </InputLabel>
                                <TextField
                                    id="lastName"
                                    name="lastName"
                                    value={values.lastName}
                                    onChange={handleChange}
                                    error={Boolean(errors.lastName)}
                                    helperText={errors.lastName}
                                    InputProps={{
                                        classes: { input: classes.inputs },
                                        disableUnderline: true
                                    }} 
                                />
                            </Grid>
                        </Grid>
                        <Grid item container justify="space-evenly">
                            <Grid item>
                                <InputLabel className={classes.label}>
                                    email
                                </InputLabel>
                                <TextField
                                    id="email"
                                    name="email"
                                    value={values.email}
                                    onChange={handleChange}
                                    error={Boolean(errors.email)}
                                    helperText={errors.email}
                                    InputProps={{
                                        classes: { input: classes.inputs },
                                        disableUnderline: true
                                    }}
                                />
                            </Grid>
                            <Grid item>
                                <InputLabel className={classes.label}>
                                    business type
                                </InputLabel>
                                <Select
                                    id="businessType"
                                    name="businessType"
                                    autoWidth
                                    MenuProps={{
                                        anchorOrigin: {
                                            vertical: "bottom",
                                            horizontal: "left"
                                        },
                                        transformOrigin: {
                                            vertical: "top",
                                            horizontal: "left"
                                        },
                                        getContentAnchorEl: null
                                    }}
                                    value={values.businessType}
                                    onChange={handleChange}
                                    classes={{ select: classes.select }}
                                >
                                    <MenuItem value={0}>Hotel</MenuItem>
                                    <MenuItem value={1}>Restaurant</MenuItem>
                                    <MenuItem value={2}>Other</MenuItem>
                                </Select>
                            </Grid>
                        </Grid>
                        <Grid item container justify="space-evenly">
                        <Grid item>
                                <InputLabel className={classes.label}>
                                        property name
                                </InputLabel>
                                <TextField
                                    id="propertyName"
                                    name="propertyName"
                                    value={values.propertyName}
                                    onChange={handleChange}
                                    error={Boolean(errors.propertyName)}
                                    fullWidth
                                    helperText={errors.propertyName}
                                    InputProps={{
                                        classes: { input: classes.inputs },
                                        disableUnderline: true
                                    }}
                                />
                            </Grid>
                            <Grid item>
                                <InputLabel className={classes.label}>
                                        address
                                </InputLabel>
                                <TextField
                                    id="address"
                                    name="address"
                                    value={values.address}
                                    onChange={handleChange}
                                    error={Boolean(errors.address)}
                                    fullWidth
                                    helperText={errors.address}
                                    InputProps={{
                                        classes: { input: classes.inputs },
                                        disableUnderline: true
                                    }}
                                />
                            </Grid>
                        </Grid>
                        <Typography align="center" className={classes.typography}>
                            ist airport shuttle cost in TL
                        </Typography>
                        <Grid item container justify="space-evenly">
                            <Grid item xs={2} sm={2} md={2} lg={2}>
                                <InputLabel className={classes.label}>
                                     (1-4) pax
                                </InputLabel>
                                <TextField
                                    id="IstAirportMaxFourPaxCost"
                                    name="IstAirportMaxFourPaxCost"
                                    value={values.IstAirportMaxFourPaxCost}
                                    onChange={handleChange}
                                    error={Boolean(errors.IstAirportMaxFourPaxCost)}
                                    helperText={errors.IstAirportMaxFourPaxCost}
                                    InputProps={{
                                        classes: { input: classes.inputs },
                                        disableUnderline: true
                                    }}
                                />
                            </Grid>
                            <Grid item xs={2} sm={2} md={2} lg={2}>
