import { Grid, List, Typography, ListItem, Button, ListItemAvatar, Avatar, ListItemText, Divider, Select } from '@material-ui/core';
import { useEffect, useState } from 'react';
import { getDrivers } from '../../../../helpers/APICalls/driver';
import useStyles from './useStyles';
import { useSnackBar } from '../../../../context/useSnackbarContext';  

const AssignDriver = (): JSX.Element => {
    const { root, select } = useStyles();
    const { updateSnackBarMessage } = useSnackBar();
    const [value, setValue] = useState<string>('1');

    useEffect(() => {
        getDrivers().then((data) => {
            if (data.error) {
                updateSnackBarMessage(data.error);
            } else if (data.success){
                console.log(data.success);
                updateSnackBarMessage('Client has been added successfully!')
            } else {
                updateSnackBarMessage('An unexpected error occurred. Please try again !');
            }
        });
    }, []);

    return (
        <>
            <Grid container direction="column" className={root} >
                <Grid item>
                    <Typography>
                        Please Select Driver
                    </Typography>
                        <List style={{ width: '100%', maxWidth: 360, backgroundColor: 'background.paper' }}>
                            <ListItem alignItems="flex-start" onClick={() => {setValue('5')}}>
                                <ListItemAvatar>
                                    <Avatar alt="Remy Sharp" src="https://airportshuttle0.s3.amazonaws.com/defaultAvatar.png" />
                                </ListItemAvatar>
                                <ListItemText
                                    primary="Brunch this weekend?"
                                    secondary={
                                        <>
                                        <Typography
                                            style={{ display: 'inline' }}
                                            component="span"
                                            variant="body2"
                                        >
                                            Ali Connors
                                        </Typography>
                                        {" — I'll be in your neighborhood doing errands this…"}
                                        </>
                                        }
                                        />
                            </ListItem>
                        <Divider variant="inset" component="li" />
                            <ListItem alignItems="flex-start" onClick={() => {setValue('2')}}>
                                    <ListItemAvatar>
                                        <Avatar alt="Remy Sharp" src="https://airportshuttle0.s3.amazonaws.com/defaultAvatar.png" />
                                    </ListItemAvatar>
                                    <ListItemText
                                        primary="Brunch this weekend?"
                                        secondary={
                                            <>
                                            <Typography
                                                style={{ display: 'inline' }}
                                                component="span"
                                                variant="body2"
                                            >
                                                Ali Connors
                                            </Typography>
                                            {" — I'll be in your neighborhood doing errands this…"}
                                            </>
                                            }
                                            />
                                </ListItem>
                            </List>
                    <Button onClick={() => {console.log(value)}}>
                        click me
                    </Button>
                </Grid>
            </Grid>
        </>
    );
};

export default AssignDriver;
