import { Button, CircularProgress } from '@material-ui/core';
import useStyles from './useStyles';
import clsx from 'clsx';

interface Props {
    btnText: string;
    submitState?: boolean | false;
    style?: string;
    cssStyle?: React.CSSProperties;
    onClick?: () => void

}

function CustomButton({ btnText,  style, onClick, submitState }: Props): JSX.Element {
    const { button, circularProgress } = useStyles();
    const btnStyle = clsx(button, style)

    return (
        <Button
        className={btnStyle}
        onClick={onClick}
        type={submitState  === true ? "submit" : ''}
        >
            {submitState === true ? <CircularProgress  className={circularProgress}/> : {btnText}} 
        </Button>

    )
}

export default CustomButton;
