import axios from 'axios';
import { AuthApiData, GetClientsApiData } from '../../interface/AuthApiData';
import { Client } from '../../interface/Client';

export const postClient = async (inputs: Client): Promise<AuthApiData> => {
    return await axios.post('https://airport-shuttle-server.herokuapp.com/users/', inputs)
            .then((res) => res.data)
            .catch(() => ({
                error: {message: 'Unable to connect to server. Please try again'}
            }))
};

export const getClients = async (): Promise<GetClientsApiData> => {
    return await axios.get('https://airport-shuttle-server.herokuapp.com/users/')
            .then((res) => res.data)
            .catch(() => ({
                error: {message: 'Unable to connect to server. Please try again'}
            }))
};

export const deleteClient = async (id: string | undefined) => {
    return await axios.delete(`https://airport-shuttle-server.herokuapp.com/users/${id}`)
        .then((res) => res.data)
        .catch(() => ({
            error: {message: 'Unable to connect to server. Please try again'}
        }));
};

export const patchClient = async (inputs: Client, id: string | undefined) => {
    return await axios.patch(`https://airport-shuttle-server.herokuapp.com/users/${id}`, inputs)
        .then((res) => res.data)
        .catch(() => ({
            error: {message: 'Unable to connect to server. Please try again'}
        }));
};
